mapper.insert:
INSERT INTO booking a
SET a.strf_id = #{strfId}
, a.check_in = #{checkIn}
, a.check_out = #{checkOut}
, a.final_payment = #{finalPayment}
, a.message = #{message}
, b.title = #{strfName}
JOIN stay_tour_restaur_fest b
ON a.strf_id = b.strf_id
WHERE a.booking_id = #{bookingId}

mapper.select:
SELECT menu_id AS menuId , strf_id AS strfId , m.price AS price FROM menu m WHERE menu_id IN (
#{menu.menuId}
)
