mapper.select:
SELECT review_id AS reviewId , title FROM review_pic WHERE review_id IN
#{item}
ORDER BY review_id DESC

mapper.update:
UPDATE review
SET content = #{content},
rating = #{rating},
updated_at = NOW()
WHERE review_id = #{reviewId}
AND user_id = #{userId}

mapper.delete:
DELETE FROM review
WHERE review_id = #{reviewId}
AND user_id = #{userId}
