mapper.select:
SELECT s.strf_id , s.category AS category , s.title AS title , s.lat AS lat , s.lng AS lng , s.address AS address , s.`explain` AS `explain` , sp.pic_name AS picTitle , COALESCE(w.wishlist_count, 0) AS wishCnt , COALESCE(r1.review_count, 0) AS ratingCnt , COALESCE(r1.avgRating, 0) AS avgRating , CASE WHEN uw.user_id IS NOT NULL THEN 1 ELSE 0 END AS wishIn , CASE WHEN r2.user_id IS NOT NULL THEN 1 ELSE 0 END AS ratingIn , s.start_at , s.end_at FROM stay_tour_restaur_fest s INNER JOIN location_detail ld ON ld.location_detail_id = s.location_detail_id AND ld.location_id IN
#{id.locationId}
LEFT JOIN ( SELECT strf_id , COUNT(*) AS wishlist_count FROM wishlist GROUP BY strf_id ) w ON s.strf_id = w.strf_id LEFT JOIN strf_pic sp ON sp.strf_id = s.strf_id LEFT JOIN ( SELECT strf_id , COUNT(*) AS review_count , avg(rating) AS avgRating FROM review GROUP BY strf_id ) r1 ON s.strf_id = r1.strf_id LEFT JOIN ( SELECT strf_id, user_id FROM wishlist WHERE user_id = #{userId} ) uw ON s.strf_id = uw.strf_id LEFT JOIN ( SELECT strf_id, user_id FROM review WHERE user_id = #{userId} ) r2 ON s.strf_id = r2.strf_id
s.category = #{category}
AND s.title LIKE '%${searchWord}%' OR s.address LIKE '%${searchWord}%'
GROUP BY s.strf_id ORDER BY avgRating DESC, wishIn DESC LIMIT #{startIdx}, #{size}

SELECT * FROM ( SELECT a.strf_id AS strfId, a.title AS strfTitle, a.category, c.title AS locationName, f.pic_name AS strfPic, AVG(d.rating) AS ratingAvg, COUNT(d.review_id) AS reviewCount, COUNT(e.user_id) AS wishlistCount, COUNT(d.rating) AS ratingCnt , COALESCE(a.created_at, NOW()) AS createdAt,
IF(EXISTS(SELECT 1 FROM wishlist WHERE user_id = #{userId} AND strf_id = a.strf_id), 1, 0) AS wishIn
0 AS wishIn
FROM stay_tour_restaur_fest a JOIN location_detail b ON a.location_detail_id = b.location_detail_id JOIN location c ON b.location_id = c.location_id LEFT JOIN review d ON a.strf_id = d.strf_id LEFT JOIN wishlist e ON a.strf_id = e.strf_id LEFT JOIN strf_pic f ON f.strf_id = a.strf_id WHERE a.category = #{category}
AND ( a.title LIKE CONCAT('%', #{searchWord}, '%') OR a.address LIKE CONCAT('%', #{searchWord}, '%') OR c.title LIKE CONCAT('%', #{searchWord}, '%') OR a.title LIKE CONCAT('%', #{searchWord}, '%') OR a.category LIKE CONCAT('%', #{searchWord}, '%') )
GROUP BY a.strf_id ) AS subquery ORDER BY createdAt DESC, CASE WHEN #{orderType} = 'likeCnt' THEN wishlistCount END DESC, CASE WHEN #{orderType} = 'ratingAvg' THEN ratingAvg END DESC, CASE WHEN #{orderType} = 'ratingCnt' THEN reviewCount END DESC LIMIT #{startIdx}, #{size}

SELECT a.strf_id AS strfId, a.title AS strfTitle, a.category, b.detail_title AS locationName, h.pic_name AS strfPic, AVG(f.rating) AS averageRating, COUNT(f.review_id) AS reviewCnt, (SELECT COUNT(*) FROM wishlist w WHERE w.strf_id = a.strf_id) AS wishlistCnt,
IF(EXISTS(SELECT 1 FROM wishlist WHERE user_id = #{userId} AND strf_id = a.strf_id), 1, 0) AS wishIn
0 AS wishIn , j.amenity_id AS amenityId, MAX(a.created_at) AS createdAt FROM stay_tour_restaur_fest a LEFT JOIN location_detail b ON a.location_detail_id = b.location_detail_id JOIN location e ON e.location_id = b.location_id LEFT JOIN review f ON a.strf_id = f.strf_id LEFT JOIN wishlist g ON a.strf_id = g.strf_id AND g.user_id = #{userId} LEFT JOIN strf_pic h ON a.strf_id = h.strf_id LEFT JOIN amenipoint i ON i.strf_id = a.strf_id LEFT JOIN amenity j ON i.amenity_id = j.amenity_id WHERE a.category = 'STAY'
AND ( a.title LIKE CONCAT('%', #{searchWord}, '%') OR a.address LIKE CONCAT('%', #{searchWord}, '%') OR b.detail_title LIKE CONCAT('%', #{searchWord}, '%') )
AND ( j.amenity_id IN
#{id}
)
GROUP BY a.strf_id ORDER BY MAX(a.created_at) DESC LIMIT #{startIdx}, #{size}

SELECT d.amenity_id AS amenityId, d.title AS amenityTitle FROM stay_tour_restaur_fest a JOIN amenipoint c ON a.strf_id = c.strf_id JOIN amenity d ON c.amenity_id = d.amenity_id WHERE d.amenity_id IN
#{id}
GROUP BY d.amenity_id
