mapper.select:
SELECT t.trip_id AS tripId , t.title AS title , t.start_at AS startAt , t.end_at AS endAt , l.location_pic AS locationPic , count(sm.schedule_memo_id) AS scheduleCnt FROM trip t JOIN trip_user tu ON tu.trip_id = t.trip_id AND tu.user_id = #{userId} JOIN trip_location tl ON tl.trip_id = t.trip_id LEFT JOIN location l ON l.location_id = tl.location_id JOIN sche_memo sm ON sm.trip_id = t.trip_id AND sm.category = 'SCHE'
WHERE t.end_at < #{currentAt}
WHERE t.end_at > #{currentAt}
GROUP BY t.trip_id

mapper.insert:
INSERT INTO trip
SET title = #{title}
    , manager_id = #{managerId}
    , start_at = #{startAt}
    , end_at = #{endAt}
