mapper.select:
SELECT COUNT(*) > 0
  FROM user
 WHERE email = #{email}

SELECT COUNT(*) AS emailCount
  FROM user
 WHERE email = #{email}

mapper.insert:
INSERT INTO user
   SET email = #{email}
     , pw = #{pw}
     , name = #{name}
     , profile_pic = #{profilePic}
     , state = 0

mapper.select:
SELECT user_id AS userId, email, pw, name, profile_pic AS profilePic
  FROM user
 WHERE email = #{email}

SELECT user_id AS signedUserId, email, name, profile_pic AS profilePic
  FROM user
 WHERE user_id = #{signedUserId}

SELECT user_id AS signedUserId, pw
  FROM user
 WHERE user_id = #{signedUserId}

mapper.update:
UPDATE user
   SET pw = #{newPw}
 WHERE user_id = #{signedUserId}

UPDATE user


    pw = #{newPw}


    , name = #{name}


    , profile_pic = #{profilePic}


WHERE email = #{email}

mapper.select:
SELECT user_id AS userId
  FROM user
 WHERE email = #{email}

mapper.update:
