mapper.select:
SELECT COUNT(*) > 0
FROM wishlist
WHERE user_id = #{userId} AND strf_id = #{strfId}

SELECT w.strf_id AS strfId, p.category AS category, sp.pic_name AS strfPic, p.title AS title, p.address AS locationTitle, COUNT(r.rating) AS ratingCnt, AVG(r.rating) AS averageRating FROM wishlist w JOIN stay_tour_restaur_fest p ON w.strf_id = p.strf_id LEFT JOIN strf_pic sp ON p.strf_id = sp.strf_id LEFT JOIN review r ON w.strf_id = r.schedule_id WHERE w.user_id = #{userId}
AND p.category IN
#{item}
GROUP BY w.strf_id, p.category, sp.pic_name, p.title, p.address LIMIT #{offset}, 10
