mapper.select:
SELECT COUNT(*) > 0
FROM wishlist
WHERE user_id = #{userId} AND strf_id = #{strfId}

SELECT s.strf_id AS strfId, s.category AS category, p.pic_name AS strfPic, s.title AS title, l.detail_title AS locationTitle, IFNULL(r.rating_cnt, 0) AS ratingCnt, IFNULL(r.average_rating, 0) AS averageRating, IFNULL(w.wish_cnt, 0) AS wishCnt FROM quadruaple.stay_tour_restaru_fest s LEFT JOIN quadruaple.location_detail l ON s.location_detail_id = l.location_detail_id LEFT JOIN quadruaple.strf_pic p ON s.strf_id = p.strf_id LEFT JOIN ( SELECT strf_id, COUNT(*) AS rating_cnt, AVG(rating) AS average_rating FROM quadruaple.review GROUP BY strf_id ) r ON s.strf_id = r.strf_id LEFT JOIN ( SELECT strf_id, COUNT(*) AS wish_cnt FROM quadruaple.wishlist GROUP BY strf_id ) w ON s.strf_id = w.strf_id WHERE s.category IN
#{category}
LIMIT #{offset}, #{limit};
